mapper.select:
SELECT a.seq , a.category_seq AS categorySeq , a.member_seq AS memberSeq , (SELECT nickname FROM ch_member WHERE seq = a.member_seq) AS nickname , a.title , a.content , a.recommend , a.un_recommend AS unRecommend , a.view_cnt AS viewCnt , (SELECT COUNT(*) FROM ch_board_comment WHERE board_seq = a.seq) AS commentCount , a.reg_date AS regDate , a.mod_date AS modDate FROM ch_board a WHERE 1=1
AND a.category_seq = #{categorySeq}
ORDER BY a.seq DESC LIMIT ${pageNum * rowCount}, ${rowCount}

mapper.insert:
INSERT INTO ch_board (
	category_seq
	, member_seq
	, title
	, content
	, reg_date
) VALUES (
	#{categorySeq}
	, #{memberSeq}
	, #{title}
	, #{content}
	, NOW()
)

mapper.update:
UPDATE ch_board SET
	category_seq = #{categorySeq}
	, member_seq = #{memberSeq}
	, title = #{title}
	, content = #{content}
	, mod_date=NOW()
WHERE seq = #{seq}

mapper.select:
SELECT a.seq , a.parent_seq AS parentSeq , a.board_seq AS boardSeq , a.member_seq AS memberSeq , (SELECT COUNT(*) FROM ch_board_comment WHERE parent_seq = a.seq) AS childCount , (SELECT nickname FROM ch_member WHERE seq = a.member_seq) AS nickname , a.nickname AS nonSignUpNickname , a.password , a.content , a.recommend , a.un_recommend AS unRecommend , a.del_yn AS delYn , a.reg_date AS regDate , a.mod_date AS modDate FROM ch_board_comment a WHERE 1=1
AND a.board_seq = #{boardSeq}
AND a.parent_seq = #{parentSeq}
ORDER BY a.seq DESC

SELECT COUNT(*)
FROM ch_board_comment
WHERE member_seq = #{value}

mapper.insert:
INSERT INTO ch_board_comment (
		board_seq

			, parent_seq


			, member_seq


			, nickname


			, password

		, content
		, del_yn
		, reg_date
	) VALUES (
		#{boardSeq}

			, #{parentSeq}


			, #{memberSeq}


			, #{nickname}


			, #{password}

		, #{content}
		, 'N'
		, NOW()
)

mapper.update:
UPDATE ch_board_comment SET
	del_yn = 'Y'
	, del_date = NOW()
WHERE seq = #{seq}

	AND password = #{password}


	AND member_seq = #{memberSeq}
