mapper.select:
SELECT COUNT(*)
FROM ch_stats_buffer
WHERE type_code = #{typeCode}
	AND ip = #{ip}

	AND board_seq = #{boardSeq}

mapper.insert:
		INSERT INTO ch_stats_buffer (
        type_code
        , ip

        	, board_seq

        , reg_date
    ) VALUES (
        #{typeCode}
        , #{ip}

        	, #{boardSeq}

        , NOW()
    )
